import React, { Component } from "react";
import 'antd/dist/antd.css';
import axios from 'axios';
import { Layout, Typography, Space, Card } from 'antd';

const { Header, Footer, Sider, Content } = Layout;

const { Title } = Typography;

class HomeContainer extends Component {

  componentDidMount() {
    axios.get('https://pokeapi.co/api/v2/pokemon/2/').
      then(result => {
        console.log(result)
        this.setState({ pokemon: result.data })
      }).catch(console.log);
  }

}

function App() {

  return (
    <div className="App">

      <Layout>
        <Sider style={{ background: '#8CFFCD' }}><Title level={2}>Pokes</Title></Sider>
        <Layout>
          <Header style={{ background: '#FFA59B' }}><Title>Reyes Rojas Mauricio</Title></Header>
          <Content><Space direction="horizontal">
            <Card title="Card" style={{ width: 300 }}>
              <p>pokemon.name})</p>
              <p>Card content</p>
            </Card>
            <Card title="Card" style={{ width: 300 }}>
              <p>Card content</p>
              <p>Card content</p>
            </Card>
            <Card title="Card" style={{ width: 300 }}>
              <p>Card content</p>
              <p>Card content</p>
            </Card>
            <Card title="Card" style={{ width: 300 }}>
              <p>Card content</p>
              <p>Card content</p>
            </Card>
          </Space></Content>
          <Content><Space direction="horizontal">
            <Card title="Card" style={{ width: 300 }}>
              <p>Card content</p>
              <p>Card content</p>
            </Card>
            <Card title="Card" style={{ width: 300 }}>
              <p>Card content</p>
              <p>Card content</p>
            </Card>
          </Space></Content>
          <Footer style={{ background: '#FFA59B' }}><Title level={3}>Derechos Reservados©</Title></Footer>
        </Layout>
      </Layout>

    </div >

  );
}
export default App;
